    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int n1 = Integer.parseInt(numerador.getValue().toString());
        int n2 = Integer.parseInt(denominador.getValue().toString());
        int m = n1 / n2 , r = n1 % n2;
        rDivisao.setText(Integer.toString(m));
        rResto.setText(Integer.toString(r));
    }//GEN-LAST:event_jButton1ActionPerformed

