    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int a, b, c;
        a = jSlider1.getValue();
        b = jSlider2.getValue();
        c = jSlider3.getValue();
        if (( a < b + c ) && ( b < a + c) && ( c < b + a)){
            jLabel2.setText("Forma um Tri칙ngulo!");
            if ((a == b) && (b == c)){
                jLabel1.setText("Equil치tero");
            }
            else if ((a == b) || (a == c) || (b == c)){
                jLabel1.setText("Is칩sceles");
            }
            else {
                jLabel1.setText("Escaleno");
            }
        }
        else {
        jLabel2.setText("Nao forma um Tri칙ngulo");
        jLabel1.setText("-");            
        }

    }//GEN-LAST:event_jButton1ActionPerformed

